DO $$
DECLARE
  view_name text;
  view_names text[] := ARRAY['v5_leads_campaign_daily'];
  error_count int := 0;
BEGIN
  FOREACH view_name IN ARRAY view_names
  LOOP
    BEGIN
      IF EXISTS (
        SELECT 1 FROM pg_matviews 
        WHERE schemaname = 'dashboards' 
          AND matviewname = view_name
      ) THEN
        EXECUTE format('REFRESH MATERIALIZED VIEW CONCURRENTLY dashboards.%I', view_name);
        RAISE NOTICE '✅ Refreshed: %', view_name;
      ELSE
        RAISE WARNING '⚠️ View does not exist: %', view_name;
      END IF;
    EXCEPTION WHEN OTHERS THEN
      error_count := error_count + 1;
      RAISE WARNING '❌ Error refreshing %: %', view_name, SQLERRM;
    END;
  END LOOP;

  IF error_count > 0 THEN
    RAISE NOTICE '⚠️ Completed with % errors', error_count;
  ELSE
    RAISE NOTICE '✅ All views refreshed successfully';
  END IF;
END $$;

DO $$
DECLARE
  view_name text;
  view_names text[] := ARRAY[
    'v5_leads_source_daily',
    'crm_marketing_link_kvm'
    'v5_campaign_anomalies',
    'v5_campaign_total',
    'v5_campaign_weekly',
    'v5_leads_campaign_daily',
    'v5_manager_daily',
    'v5_platform_daily_kpis',
    'v5_platform_wow',
    'v5_source_daily_alloc',
    'v5_source_total_alloc',
    'v5_product_daily'
  ];
  error_count int := 0;
  success_count int := 0;
BEGIN
  SET LOCAL statement_timeout = '30min';  -- ✅ долгая операция

  FOREACH view_name IN ARRAY view_names
  LOOP
    BEGIN
      IF EXISTS (
        SELECT 1 FROM pg_matviews 
        WHERE schemaname = 'dashboards' 
          AND matviewname = view_name
      ) THEN
        /* v5_product_daily - без CONCURRENTLY (нет уникального индекса) */
        IF view_name = 'v5_product_daily' THEN
          EXECUTE format('REFRESH MATERIALIZED VIEW dashboards.%I', view_name);
        ELSE
          EXECUTE format('REFRESH MATERIALIZED VIEW CONCURRENTLY dashboards.%I', view_name);
        END IF;

        success_count := success_count + 1;
        RAISE NOTICE '✅ [%/%] Refreshed: %', success_count, array_length(view_names,1), view_name;
      ELSE
        RAISE WARNING '⚠️ View does not exist: %', view_name;
      END IF;
    EXCEPTION WHEN OTHERS THEN
      error_count := error_count + 1;
      RAISE WARNING '❌ Error refreshing %: %', view_name, SQLERRM;
    END;
  END LOOP;

  RAISE NOTICE '========================================';
  RAISE NOTICE '✅ Success: % views', success_count;
  RAISE NOTICE '❌ Errors: % views', error_count;
  RAISE NOTICE '========================================';

  IF error_count > array_length(view_names,1) / 2 THEN
    RAISE EXCEPTION 'Too many errors (>50%%), aborting workflow';
  END IF;
END $$;
